function largestOfFour(arr) {
  // You can do this!
  var arrayMax = [];
  //var max = 0;

  for(var i = 0; i < arr.length; i++){
    var max = 0;
    for(var j = 0; j < arr[i].length; j++){
      if(j === 0){
        max = arr[i][j];
      }
      if(arr[i][j] > max){
        max = arr[i][j];
      }
    }
    arrayMax[i] = max;
   // max = 0;
  }

  return arrayMax;
}

